elseif strcmp(module, 'Spectra (FieldTrip)') %Load pow structure to carry all needed vars to SFT_GenerateSpectra function pow = []; pow.pid = pid; pow.EEG = EEG; pow.timelbms = timelbms; pow.timeubms = timeubms; pow.specchans = specchans; pow.specfreqs = specfreqs; pow.spectime = spectime; pow.spec60filt = spec60filt; pow.spectimestg = spectimestg; pow.fts_winlength = fts_winlength; pow.fts_winoverlap = fts_winoverlap; pow.ftspecplotstyle = ftspecplotstyle; pow.roistring = roistring; pow.eegrootfolder = app.eegrootfolder; pow.bcimode = bcimode; pow.pidbcichans = pidbcichans; pow.setfp = setfp; pow.procstring = procstring; pow.imgmode = imgmode; if exist('fts_group') pow.fts_group = fts_group; end
